package idp

import (
	"crypto/rsa"
	"encoding/base64"
	"encoding/json"
)

type Provider struct {
	Issuer        string
	JWKSUrl       string
	KID           string
	RsaPrivateKey *rsa.PrivateKey
	PrivateKey    string
	RsaPublicKey  *rsa.PublicKey
	PublicKey     string
}

var Provider1 *Provider = &Provider{
	Issuer:     "https://bensolo-io.github.io/jwt-kit/provider1",
	JWKSUrl:    "https://bensolo-io.github.io/jwt-kit/provider1/.well-known/jwks.json",
	KID:        "fOpWQOJ_sSkAzzv5NU1jfy9P6nOwz9udUew26ferYMs",
	PrivateKey: "",
	PublicKey:  "",
}

var Provider2 *Provider = &Provider{
	Issuer:     "https://bensolo-io.github.io/jwt-kit/provider2",
	JWKSUrl:    "https://bensolo-io.github.io/jwt-kit/provider2/.well-known/jwks.json",
	KID:        "WTwPMNG_FBQTM9VNl_cAnFIoHuZx4JFwrXqVDA48CFE",
	PrivateKey: "",
	PublicKey:  "",
}

func init() {
	Provider1.SetRsaKeys()
	Provider2.SetRsaKeys()
}

func (p *Provider) SetRsaKeys() {
	p.SetPrivateRsaKey()
	p.SetPublicRsaKey()
}

func (p *Provider) SetPrivateRsaKey() {
	b, err := base64.StdEncoding.DecodeString(p.PrivateKey)
	if err != nil {
		panic(err)
	}
	var k rsa.PrivateKey
	if err = json.Unmarshal(b, &k); err != nil {
		panic(err)
	}
	p.RsaPrivateKey = &k
}

func (p *Provider) SetPublicRsaKey() {
	b, err := base64.StdEncoding.DecodeString(p.PublicKey)
	if err != nil {
		panic(err)
	}
	var k rsa.PublicKey
	if err = json.Unmarshal(b, &k); err != nil {
		panic(err)
	}
	p.RsaPublicKey = &k
}

