package idp

import (
	"crypto/rsa"
	"encoding/base64"
	"encoding/json"
)

const (
	Issuer = "https://bensolo-io.github.io/jwt-kit"

	JWKSUrl = "https://bensolo-io.github.io/jwt-kit/.well-known/jwks.json"

	Kid = "fOpWQOJ_sSkAzzv5NU1jfy9P6nOwz9udUew26ferYMs"

	privateKey = ``

	publicKey = ``
)

func GetRSAPrivateKey() *rsa.PrivateKey {
	b, err := base64.StdEncoding.DecodeString(privateKey)
	if err != nil {
		panic(err)
	}
	var k rsa.PrivateKey
	if err = json.Unmarshal(b, &k); err != nil {
		panic(err)
	}
	return &k
}

func GetRSAPublicKey() *rsa.PublicKey {
	b, err := base64.StdEncoding.DecodeString(publicKey)
	if err != nil {
		panic(err)
	}
	var k rsa.PublicKey
	if err = json.Unmarshal(b, &k); err != nil {
		panic(err)
	}
	return &k
}
